package main;

public class Ex1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 =10;
		int num2 = -20;
		
		//부호를 그대로 유지
		System.out.println(+num1);
		System.out.println(+num2);
		
		//부호를 반대로 바꾸기
		System.out.println(-num1);
		System.out.println(-num2);
		
	}

}
